import React, { useEffect, useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Circle, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Custom marker icons
const userIcon = L.icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    iconRetinaUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

const playSpaceIcon = L.icon({
    iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    iconRetinaUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
    shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

// Component to handle map center updates
function MapUpdater({ center, userLocation }) {
    const map = useMap();

    useEffect(() => {
        if (center) {
            map.setView(center, 15, { animate: true });
        }
    }, [center, map]);

    // Handle initial location and location updates
    useEffect(() => {
        if (userLocation) {
            map.flyTo([userLocation.lat, userLocation.lng], 15, {
                duration: 1.5,
                easeLinearity: 0.25
            });
        }
    }, [userLocation, map]);

    return null;
}

function Map({ userLocation, playSpaces }) {
    const defaultPosition = [28.6139, 77.2090]; // Default to Delhi

    // Memoize the center position
    const center = useMemo(() => {
        return userLocation ? [userLocation.lat, userLocation.lng] : defaultPosition;
    }, [userLocation, defaultPosition]);

    return (
        <MapContainer 
            center={center}
            zoom={15} 
            style={{ height: '100%', width: '100%' }}
        >
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            <MapUpdater center={center} userLocation={userLocation} />

            {/* User Location Marker with Accuracy Circle */}
            {userLocation && (
                <>
                    <Marker 
                        position={[userLocation.lat, userLocation.lng]}
                        icon={userIcon}
                    >
                        <Popup>
                            <div>
                                <h3>Your Location</h3>
                                <p>Accuracy: Â±{Math.round(userLocation.accuracy)}m</p>
                            </div>
                        </Popup>
                    </Marker>

                    {/* Accuracy circle */}
                    <Circle
                        center={[userLocation.lat, userLocation.lng]}
                        radius={userLocation.accuracy}
                        pathOptions={{ 
                            color: '#2196F3',
                            fillColor: '#2196F3',
                            fillOpacity: 0.15
                        }}
                    />
                </>
            )}

            {/* Play Spaces Markers */}
            {playSpaces && playSpaces.map((space, index) => (
                <Marker 
                    key={index}
                    position={[space.location.latitude, space.location.longitude]}
                    icon={playSpaceIcon}
                >
                    <Popup>
                        <div>
                            <h3>{space.name}</h3>
                            <p>{space.sport}</p>
                            <p>{space.address}</p>
                            <p>Available: {space.availableTime}</p>
                        </div>
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
}

export default Map;
