import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Box, VStack, Container, Text, useToast, Spinner, Center } from '@chakra-ui/react';
import Map from './components/Map';

function App() {
  const [userLocation, setUserLocation] = useState(null);
  const [locationError, setLocationError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const toast = useToast();
  const watchIdRef = useRef(null);
  const hasInitialLocation = useRef(false);

  const handleLocationSuccess = useCallback((position) => {
    const newLocation = {
      lat: position.coords.latitude,
      lng: position.coords.longitude,
      accuracy: position.coords.accuracy
    };

    setUserLocation(newLocation);
    setLocationError(null);
    setIsLoading(false);
    hasInitialLocation.current = true;
  }, []);

  const handleLocationError = useCallback((error) => {
    console.error('Location error:', error);
    setLocationError(error.message);
    setIsLoading(false);
    toast({
      title: 'Location Error',
      description: `Could not get your location: ${error.message}`,
      status: 'error',
      duration: 5000,
      isClosable: true,
    });
  }, [toast]);

  const getLocation = useCallback(() => {
    if (!navigator.geolocation) {
      setLocationError('Geolocation is not supported by your browser');
      setIsLoading(false);
      toast({
        title: 'Browser Not Supported',
        description: 'Geolocation is not supported by your browser',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
      return;
    }

    const options = {
      enableHighAccuracy: true,
      timeout: 10000,
      maximumAge: 0
    };

    // Get initial position
    if (!hasInitialLocation.current) {
      setIsLoading(true);
      navigator.geolocation.getCurrentPosition(
        handleLocationSuccess,
        handleLocationError,
        options
      );
    }

    // Set up continuous watching
    if (watchIdRef.current === null) {
      watchIdRef.current = navigator.geolocation.watchPosition(
        handleLocationSuccess,
        handleLocationError,
        options
      );
    }

    return () => {
      if (watchIdRef.current !== null) {
        navigator.geolocation.clearWatch(watchIdRef.current);
        watchIdRef.current = null;
      }
    };
  }, [handleLocationSuccess, handleLocationError, toast]);

  // Initialize location tracking
  useEffect(() => {
    const cleanup = getLocation();
    return () => {
      cleanup();
      hasInitialLocation.current = false;
    };
  }, [getLocation]);

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="container.xl" py={5}>
        <VStack spacing={4}>
          {locationError && (
            <Text color="red.500">
              {locationError}
            </Text>
          )}

          <Box w="100%" h="600px" borderRadius="lg" overflow="hidden" boxShadow="lg" position="relative">
            <Map 
              userLocation={userLocation} 
              playSpaces={[]} 
            />
            {isLoading && !userLocation && (
              <Center
                position="absolute"
                top="0"
                left="0"
                right="0"
                bottom="0"
                bg="rgba(0, 0, 0, 0.7)"
                zIndex="overlay"
              >
                <VStack spacing={4}>
                  <Spinner
                    thickness="4px"
                    speed="0.65s"
                    emptyColor="gray.200"
                    color="blue.500"
                    size="xl"
                  />
                  <Text color="white" fontWeight="medium">
                    Getting your location...
                  </Text>
                </VStack>
              </Center>
            )}
          </Box>

          {userLocation && (
            <Text fontSize="sm" color="gray.600">
              Your location: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
              {userLocation.accuracy && ` (Accuracy: ±${Math.round(userLocation.accuracy)}m)`}
            </Text>
          )}
        </VStack>
      </Container>
    </Box>
  );
}

export default App;
