import React, { useState, useEffect, useCallback } from 'react';
import { Box, VStack, Container, Text, useToast, Spinner, Center } from '@chakra-ui/react';
import Map from './components/Map';

function App() {
  const [userLocation, setUserLocation] = useState(null);
  const [locationError, setLocationError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const toast = useToast();

  const getLocation = useCallback(() => {
    if (!navigator.geolocation) {
      setLocationError('Geolocation is not supported by your browser');
      toast({
        title: 'Browser Not Supported',
        description: 'Geolocation is not supported by your browser',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
      return;
    }

    setIsLoading(true);
    // First, get immediate position
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setIsLoading(false);
        setUserLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
          accuracy: position.coords.accuracy
        });
        setLocationError(null);
      },
      (error) => {
        console.error('Error getting location:', error);
        setIsLoading(false);
        setLocationError(error.message);
        toast({
          title: 'Location Error',
          description: `Could not get your location: ${error.message}`,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      },
      {
        enableHighAccuracy: true,
        timeout: 5000,
        maximumAge: 0
      }
    );
  }, [toast]);

  // Get location immediately when component mounts
  useEffect(() => {
    getLocation();
  }, [getLocation]);

  // Set up location watching
  useEffect(() => {
    let watchId;

    if (navigator.geolocation) {
      watchId = navigator.geolocation.watchPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy
          });
          setLocationError(null);
        },
        (error) => {
          console.error('Error watching location:', error);
          setLocationError(error.message);
          toast({
            title: 'Location Error',
            description: `Lost track of your location: ${error.message}`,
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        }
      );
    }

    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [toast]);

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="container.xl" py={5}>
        <VStack spacing={4}>

          {locationError && (
            <Text color="red.500">
              {locationError}
            </Text>
          )}

          <Box w="100%" h="600px" borderRadius="lg" overflow="hidden" boxShadow="lg" position="relative">
            <Map 
              userLocation={userLocation} 
              playSpaces={[]} 
            />
            {isLoading && (
              <Center
                position="absolute"
                top="0"
                left="0"
                right="0"
                bottom="0"
                bg="rgba(0, 0, 0, 0.7)"
                zIndex="overlay"
              >
                <VStack spacing={4}>
                  <Spinner
                    thickness="4px"
                    speed="0.65s"
                    emptyColor="gray.200"
                    color="blue.500"
                    size="xl"
                  />
                  <Text color="white" fontWeight="medium">
                    Getting your location...
                  </Text>
                </VStack>
              </Center>
            )}
          </Box>

          {userLocation && (
            <Text fontSize="sm" color="gray.600">
              Your location: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
              {userLocation.accuracy && ` (Accuracy: Â±${Math.round(userLocation.accuracy)}m)`}
            </Text>
          )}
        </VStack>
      </Container>
    </Box>
  );
}

export default App;
