import React, { useState, useEffect, useCallback } from 'react';
import { Box, VStack, Container, Text, useToast, Spinner, Center } from '@chakra-ui/react';
import Map from './components/Map';
import { getNearbyPlaySpaces } from './services/playSpaceService';

function App() {
  const [userLocation, setUserLocation] = useState(() => {
    // Try to get initial location from localStorage
    const savedLocation = localStorage.getItem('userLocation');
    return savedLocation ? JSON.parse(savedLocation) : null;
  });
  const [playSpaces, setPlaySpaces] = useState([]);
  const [isLoading, setIsLoading] = useState(!userLocation);
  const toast = useToast();

  // Update play spaces whenever user location changes
  useEffect(() => {
    const updatePlaySpaces = () => {
      if (userLocation?.lat && userLocation?.lng) {
        const nearbySpaces = getNearbyPlaySpaces(userLocation.lat, userLocation.lng, 2);
        setPlaySpaces(nearbySpaces);

        // Show toast if spaces are found
        if (nearbySpaces.length > 0) {
          toast({
            title: 'Play Spaces Found',
            description: `Found ${nearbySpaces.length} play spaces near you`,
            status: 'success',
            duration: 3000,
            isClosable: true,
          });
        }
      }
    };

    // Only update if we have a valid location
    if (userLocation?.lat && userLocation?.lng) {
      updatePlaySpaces();
    }
  }, [userLocation?.lat, userLocation?.lng]); // Only depend on lat/lng changes

  // Save location to localStorage whenever it updates
  useEffect(() => {
    if (userLocation) {
      localStorage.setItem('userLocation', JSON.stringify(userLocation));
    }
  }, [userLocation]);

  useEffect(() => {
    let watchId = null;

    // Function to handle successful location updates
    const handleSuccess = (position) => {
      const newLocation = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: new Date().getTime() // Add timestamp for freshness check
      };
      setUserLocation(newLocation);
      setIsLoading(false);
    };

    // Function to handle location errors
    const handleError = (error) => {
      console.error('Location error:', error);
      setIsLoading(false);

      // Try to get location from localStorage if available
      const savedLocation = localStorage.getItem('userLocation');
      if (savedLocation) {
        const parsedLocation = JSON.parse(savedLocation);
        const locationAge = new Date().getTime() - parsedLocation.timestamp;
        const locationAgeMinutes = Math.round(locationAge / (1000 * 60));

        setUserLocation(parsedLocation);
        toast({
          title: 'Using Saved Location',
          description: `Using your last known location from ${locationAgeMinutes} minutes ago`,
          status: 'info',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Location Error',
          description: error.message,
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    };

    // Check if geolocation is supported
    if (!navigator.geolocation) {
      setIsLoading(false);
      const savedLocation = localStorage.getItem('userLocation');
      if (savedLocation) {
        setUserLocation(JSON.parse(savedLocation));
      }
      toast({
        title: 'Browser Not Supported',
        description: 'Using last known location',
        status: 'warning',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Simple options for geolocation
    const options = {
      enableHighAccuracy: true,
      maximumAge: 0
    };

    // Start watching position
    watchId = navigator.geolocation.watchPosition(
      handleSuccess,
      handleError,
      options
    );

    // Cleanup function
    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [toast]); // Only re-run if toast changes

  return (
    <Box position="fixed" top={0} left={0} right={0} bottom={0} overflow="hidden" bg="gray.50">
      <Box w="100%" h="100%" position="relative">
            <Map 
              userLocation={userLocation} 
              playSpaces={playSpaces}
            />
            {isLoading && !userLocation && (
              <Center
                position="absolute"
                top="0"
                left="0"
                right="0"
                bottom="0"
                bg="rgba(0, 0, 0, 0.7)"
                zIndex="overlay"
              >
                <VStack spacing={4}>
                  <Spinner
                    thickness="4px"
                    speed="0.65s"
                    emptyColor="gray.200"
                    color="blue.500"
                    size="xl"
                  />
                  <Text color="white" fontWeight="medium">
                    Getting your location...
                  </Text>
                </VStack>
              </Center>
            )}
          </Box>

          {userLocation && (
            <Box 
              position="fixed" 
              bottom={4} 
              left={4} 
              right={4} 
              p={3} 
              bg="white" 
              borderRadius="md" 
              boxShadow="lg"
              zIndex={1000}
            >
              <VStack spacing={2} align="start" w="100%">
                <Text fontSize="sm" color="gray.600">
                  Your location: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
                  {userLocation.accuracy && ` (Accuracy: Â±${Math.round(userLocation.accuracy)}m)`}
                </Text>
                {playSpaces.length > 0 && (
                  <Text fontSize="sm" color="gray.600">
                    Found {playSpaces.length} play spaces within 2km
                  </Text>
                )}
              </VStack>
            </Box>
          )}
      </Box>

  );
}

export default App;
