import React, { useState, useEffect } from 'react';
import { Box, VStack, Container, Heading, Text, useToast } from '@chakra-ui/react';
import Map from './components/Map';

function App() {
  const [userLocation, setUserLocation] = useState(null);
  const [locationError, setLocationError] = useState(null);
  const toast = useToast();

  useEffect(() => {
    let watchId;
    // Get user's location
    if (navigator.geolocation) {
      watchId = navigator.geolocation.watchPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
            accuracy: position.coords.accuracy
          });
          setLocationError(null);
          toast({
            title: 'Location Updated',
            description: 'Your location has been successfully updated on the map.',
            status: 'success',
            duration: 3000,
            isClosable: true,
          });
        },
        (error) => {
          console.error('Error getting location:', error);
          setLocationError(error.message);
          toast({
            title: 'Location Error',
            description: `Could not get your location: ${error.message}`,
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        },
        {
          enableHighAccuracy: true,
          timeout: 5000,
          maximumAge: 0
        }
      );
    } else {
      setLocationError('Geolocation is not supported by your browser');
      toast({
        title: 'Browser Not Supported',
        description: 'Geolocation is not supported by your browser',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }

    // Cleanup function to stop watching location
    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [toast]);

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="container.xl" py={5}>
        <VStack spacing={8}>
          <Heading as="h1" size="xl" color="blue.600">
            PlaySpace - Find Your Game
          </Heading>

          {locationError && (
            <Text color="red.500">
              {locationError}
            </Text>
          )}

          <Box w="100%" h="600px" borderRadius="lg" overflow="hidden" boxShadow="lg">
            <Map 
              userLocation={userLocation} 
              playSpaces={[]} 
            />
          </Box>

          {userLocation && (
            <Text fontSize="sm" color="gray.600">
              Your location: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
              {userLocation.accuracy && ` (Accuracy: ±${Math.round(userLocation.accuracy)}m)`}
            </Text>
          )}
        </VStack>
      </Container>
    </Box>
  );
}

export default App;
