import React, { useState, useEffect, useCallback } from 'react';
import { Box, VStack, Container, Text, useToast, Spinner, Center } from '@chakra-ui/react';
import Map from './components/Map';

function App() {
  const [userLocation, setUserLocation] = useState(() => {
    // Try to get initial location from localStorage
    const savedLocation = localStorage.getItem('userLocation');
    return savedLocation ? JSON.parse(savedLocation) : null;
  });
  const [isLoading, setIsLoading] = useState(!userLocation);
  const toast = useToast();

  // Save location to localStorage whenever it updates
  useEffect(() => {
    if (userLocation) {
      localStorage.setItem('userLocation', JSON.stringify(userLocation));
    }
  }, [userLocation]);

  useEffect(() => {
    let watchId = null;

    // Function to handle successful location updates
    const handleSuccess = (position) => {
      const newLocation = {
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        accuracy: position.coords.accuracy,
        timestamp: new Date().getTime() // Add timestamp for freshness check
      };
      setUserLocation(newLocation);
      setIsLoading(false);
    };

    // Function to handle location errors
    const handleError = (error) => {
      console.error('Location error:', error);
      setIsLoading(false);

      // Try to get location from localStorage if available
      const savedLocation = localStorage.getItem('userLocation');
      if (savedLocation) {
        const parsedLocation = JSON.parse(savedLocation);
        const locationAge = new Date().getTime() - parsedLocation.timestamp;
        const locationAgeMinutes = Math.round(locationAge / (1000 * 60));

        setUserLocation(parsedLocation);
        toast({
          title: 'Using Saved Location',
          description: `Using your last known location from ${locationAgeMinutes} minutes ago`,
          status: 'info',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Location Error',
          description: error.message,
          status: 'error',
          duration: 3000,
          isClosable: true,
        });
      }
    };

    // Check if geolocation is supported
    if (!navigator.geolocation) {
      setIsLoading(false);
      const savedLocation = localStorage.getItem('userLocation');
      if (savedLocation) {
        setUserLocation(JSON.parse(savedLocation));
      }
      toast({
        title: 'Browser Not Supported',
        description: 'Using last known location',
        status: 'warning',
        duration: 3000,
        isClosable: true,
      });
      return;
    }

    // Simple options for geolocation
    const options = {
      enableHighAccuracy: true,
      maximumAge: 0
    };

    // Start watching position
    watchId = navigator.geolocation.watchPosition(
      handleSuccess,
      handleError,
      options
    );

    // Cleanup function
    return () => {
      if (watchId) {
        navigator.geolocation.clearWatch(watchId);
      }
    };
  }, [toast]); // Only re-run if toast changes

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="container.xl" py={5}>
        <VStack spacing={4}>
          <Box w="100%" h="600px" borderRadius="lg" overflow="hidden" boxShadow="lg" position="relative">
            <Map 
              userLocation={userLocation} 
              playSpaces={[]} 
            />
            {isLoading && !userLocation && (
              <Center
                position="absolute"
                top="0"
                left="0"
                right="0"
                bottom="0"
                bg="rgba(0, 0, 0, 0.7)"
                zIndex="overlay"
              >
                <VStack spacing={4}>
                  <Spinner
                    thickness="4px"
                    speed="0.65s"
                    emptyColor="gray.200"
                    color="blue.500"
                    size="xl"
                  />
                  <Text color="white" fontWeight="medium">
                    Getting your location...
                  </Text>
                </VStack>
              </Center>
            )}
          </Box>

          {userLocation && (
            <Text fontSize="sm" color="gray.600">
              Your location: {userLocation.lat.toFixed(6)}, {userLocation.lng.toFixed(6)}
              {userLocation.accuracy && ` (Accuracy: ±${Math.round(userLocation.accuracy)}m)`}
            </Text>
          )}
        </VStack>
      </Container>
    </Box>
  );
}

export default App;
