import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix default marker icon issue
const defaultIcon = L.icon({
    iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
    iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
    shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

L.Marker.prototype.options.icon = defaultIcon;

function Map({ userLocation, playSpaces }) {
    const defaultPosition = [28.6139, 77.2090]; // Default to Delhi

    return (
        <div className="map-container" style={{ height: '400px', width: '100%' }}>
            <MapContainer 
                center={userLocation ? [userLocation.lat, userLocation.lng] : defaultPosition} 
                zoom={13} 
                style={{ height: '100%', width: '100%' }}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                />

                {/* User Location Marker */}
                {userLocation && (
                    <Marker position={[userLocation.lat, userLocation.lng]}>
                        <Popup>Your Location</Popup>
                    </Marker>
                )}

                {/* Play Spaces Markers */}
                {playSpaces && playSpaces.map((space, index) => (
                    <Marker 
                        key={index}
                        position={[space.location.latitude, space.location.longitude]}
                    >
                        <Popup>
                            <div>
                                <h3>{space.name}</h3>
                                <p>{space.sport}</p>
                                <p>{space.address}</p>
                                <p>Available: {space.availableTime}</p>
                            </div>
                        </Popup>
                    </Marker>
                ))}
            </MapContainer>
        </div>
    );
}

export default Map;
